  Drupal.behaviors.fadeableMenu = {
    attach: function(context) {
      var didScroll,
        lastScrollTop = 0,
        delta = 5,
        navbarPosition,
        navbarHeight,
        fadeableWindow,
        fadeableDocument,
        fadeableBody;

      var headerPersistent =
        $(window).width() > 780
          ? $('#header-persistent', context)
          : $('#header-mobile', context);

      // prevent breakpoint-panels from firing script multiple times
      if (headerPersistent.length > 0) {
        navbarHeight = headerPersistent.outerHeight();
        navbarPosition = headerPersistent.offset().top;
        fadeableWindow = $(window);
        fadeableDocument = $(document);
        fadeableBody = $('body');

        // Hide Header on on scroll down
        fadeableWindow
          .off('scroll.fadeable')
          .on('scroll.fadeable', function(event) {
            didScroll = true;
          });

        setInterval(function() {
          if (didScroll) {
            hasScrolled();
            didScroll = false;
          }
        }, 5);

        function hasScrolled() {
          var st = $(this).scrollTop();

          // Make sure they scroll more than delta
          if (Math.abs(lastScrollTop - st) <= delta) return;

          if (st < lastScrollTop && st <= navbarPosition) {
            headerPersistent.removeClass('nav-sticky nav-up nav-down');
          } else {
            // If they scrolled down and are past the navbar, add class .nav-up.
            // This is necessary so you never see what is "behind" the navbar.
            if (st > lastScrollTop && st > navbarPosition + navbarHeight) {
              // Scroll Down
              headerPersistent.removeClass('nav-down').addClass('nav-up');
            } else {
              // Scroll Up
              if (
                st > navbarPosition + navbarHeight &&
                st + fadeableWindow.height() < fadeableDocument.height()
              ) {
                headerPersistent
                  .removeClass('nav-up')
                  .addClass('nav-sticky nav-down');
              }
            }
          }

          lastScrollTop = st;
        }
      }
    }
  };

  // dc_comics_essentials view horizontal scrolling displays.
  Drupal.behaviors.essentialsViewScrollable = {
    attach: function(context) {
      if ($('.view-dc-comics-essentials.style--scrollable').length === 0) {
        return;
      }

      $('.view-dc-comics-essentials.style--scrollable').each(function() {
        // Establish some vars
        var $view = $(this, context);
        var $slider = $view.find('.view-content', context);
        var $pagerPrev = $view.find('.pager-prev');
        var $pagerNext = $view.find('.pager-next');
        var isSliding = false;
        var sliderWidth = $slider.width();
        var sliderAnimDurr = 700;
        var sliderCurrPos = 0;
        var sliderPrevPos = 0;
        var $rightMostVisSlide = getRightMostVisSlide();
        var slidesTotalWidth = getTotalSlidesWidth();
        var sliderEndPos = slidesTotalWidth - sliderWidth;

        $pagerPrev.hide();

        $pagerPrev.on('click tap touch', function() {
          if ($slider.hasClass('animating')) {
            return;
          }
          $slider.addClass('animating');

          if (sliderPrevPos < sliderCurrPos) {
            // $slider.animate({scrollLeft: sliderPrevPos}, sliderAnimDurr);
            $slider[0].scrollLeft = sliderPrevPos;
          }
          else {
            // $slider.animate({scrollLeft: sliderCurrPos - sliderWidth}, sliderAnimDurr);
            $slider[0].scrollLeft = sliderCurrPos - sliderWidth;
          }

          setTimeout(function() {
            $slider.removeClass('animating');
            $rightMostVisSlide = getRightMostVisSlide();
          }, sliderAnimDurr);
        });

        $pagerNext.on('click tap touch', function() {
          if ($slider.hasClass('animating')) {
            return;
          }
          $slider.addClass('animating');

          sliderPrevPos = $slider.scrollLeft();

          var slideRect = $rightMostVisSlide.next()[0].getBoundingClientRect();
          // $slider.animate({scrollLeft: slideRect.x + sliderCurrPos}, sliderAnimDurr);
          $slider[0].scrollLeft = slideRect.x + sliderCurrPos;

          setTimeout(function() {
            $slider.removeClass('animating');
            $rightMostVisSlide = getRightMostVisSlide();
          }, sliderAnimDurr);
        });

        $slider.on('scroll', function() {
          sliderCurrPos = $slider.scrollLeft();

          if (sliderCurrPos >= sliderEndPos - 50) {
            $pagerNext.hide();
          }
          else {
            $pagerNext.show();
          }

          if (sliderCurrPos == 0) {
            $pagerPrev.hide();
          }
          else {
            $pagerPrev.show();
          }
        });

        function getRightMostVisSlide() {
          var $rightMostVisSlide;

          $slider.find('.essential-slide').each(function(index) {
            var slideOffset = $(this).position().left + $(this).width();

            if (slideOffset < sliderWidth) {
              $rightMostVisSlide = $(this);
            }
          });

          return $rightMostVisSlide;
        }

        function getTotalSlidesWidth() {
          var slidesTotalWidth = 0;

          $slider.find('.essential-slide').each(function(index) {
            slidesTotalWidth += $(this).width();
          });

          return slidesTotalWidth;
        }


        // Meta overlay functionality

        $slider.on('beforeChange', function() {
          isSliding = true;
        });

        $slider.on('afterChange', function() {
          isSliding = false;
        });

        // Click handler for cards
        $slider.find('.views-row.essential-card').on(
          'click tap touch',
          function() {
            if (isSliding) {
              return;
            }

            slideIn($(this));
          }
        );

        // Close meta overlay
        var close = function() {
          $slider.find('.essential-meta.active .close').on('click tap touch', function(e) {
            $slider.find('.views-row').removeClass('active');
            $(this)
              .parent()
              .remove();
          });
        };

        // Open meta overlay
        var slideIn = function(item) {
          // Apply css for active
          $slider.find('.views-row').removeClass('active');
          $(item).addClass('active');

          // Create overlay and animate it in
          var meta = $('.essential-meta', item);
          $(meta)
            .clone(true)
            .appendTo($slider)
            .addClass('animated');
          setTimeout(function() {
            $slider.find('.essential-meta.animated').addClass('active');
            // Initialize paging & close buttons
            paging();
            close();
          }, 10);
        };

        // Handle pseudo paging
        var paging = function() {
          // init variable
          var nextCard;
          // Prev functionality
          $slider.find('.essential-meta.active .prev').on('click tap touch', function() {
            nextCard = $slider.find('.essential-card.active').prev();
            page(nextCard);
            // Call function on itself to re-init bindings
            paging();
          });
          // Next functionality
          $slider.find('.essential-meta.active .next').on('click tap touch', function() {
            nextCard = $slider.find('.essential-card.active').next();
            page(nextCard);
            // Call function on itself to re-init bindings
            paging();
          });
        };

        // Do the paging
        var page = function(nextCard) {
          // Remove the old active state & dom element
          $slider.find('.views-row').removeClass('active');
          $slider.find('.essential-meta.active').addClass('slideOut');
          setTimeout(function() {
            $slider.find('.essential-meta.slideOut').remove();
          }, 1000);

          slideIn(nextCard);
        };
      });
    }
  };

